import { Box, Container, Typography } from "@material-ui/core";
import CTAButton from "../components/CTAButton";
import { Link as RouterLink } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  background: {
    backgroundImage: "url('https://ggie.berkeley.edu/wp-content/uploads/2019/09/Basics_of_Emotion_Students_1410x820-705x410.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    minHeight: "100vh",
    position: "relative",
    "&::before": {
      content: '""',
      position: "absolute",
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: "rgba(26, 37, 47, 0.85)",
    }
  },
  content: {
    position: "relative",
    zIndex: 1,
    color: "#ECF0F1",
  },
  title: {
    fontSize: "4rem",
    fontWeight: 700,
    textAlign: "center",
    textTransform: "uppercase",
    letterSpacing: "0.2em",
    marginBottom: theme.spacing(4),
    textShadow: "2px 2px 4px rgba(0,0,0,0.3)",
    color: "#ECF0F1",
  },
  startButton: {
    marginTop: theme.spacing(4),
    padding: theme.spacing(2, 4),
    fontSize: "1.2rem",
    borderRadius: "4px",
    backgroundColor: theme.palette.primary.main,
    color: "#ECF0F1",
    transition: "background-color 0.3s ease-in-out",
    "&:hover": {
      backgroundColor: theme.palette.primary.dark,
    },
  }
}));

export function Welcome(): JSX.Element {
  const classes = useStyles();

  return (
    <Box className={classes.background}>
      <Container className={classes.content}>
        <Box
          height="100vh"
          display="flex"
          flexDirection="column"
          justifyContent="center"
          alignItems="center"
        >
          <Box width={1}>
            <Typography variant="h1" className={classes.title}>
              Students emotion detector
            </Typography>
          </Box>
          <Box mt={4} width={1} display="flex" alignItems="center" justifyContent="center">
            <Box>
              <CTAButton component={RouterLink} to="/login" className={classes.startButton}>
                Start
              </CTAButton>
            </Box>
          </Box>
        </Box>
      </Container>
    </Box>
  );
}
