import { Box, Container, Typography } from "@material-ui/core";
import CTAButton from "../components/CTAButton";
import { Link as RouterLink } from "react-router-dom";
import { makeStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme) => ({
  background: {
    backgroundImage: "url('https://ggie.berkeley.edu/wp-content/uploads/2019/09/Basics_of_Emotion_Students_1410x820-705x410.jpg')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    minHeight: "100vh",
    position: "relative",
    "&::before": {
      content: '""',
      position: "absolute",
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: "rgba(0, 0, 0, 0.5)",
    }
  },
  content: {
    position: "relative",
    zIndex: 1,
    color: "white",
  },
  title: {
    fontFamily: "'Righteous', cursive",
    fontSize: "5rem",
    fontWeight: 700,
    textAlign: "center",
    textTransform: "uppercase",
    letterSpacing: "0.2em",
    marginBottom: theme.spacing(4),
    textShadow: "3px 3px 6px rgba(0,0,0,0.4)",
    background: "linear-gradient(45deg, #FF6B6B, #4ECDC4, #45B7D1, #96E6A1)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
    animation: "float 6s ease-in-out infinite, gradient 3s ease infinite",
    backgroundSize: "300% 300%",
    cursor: "default",
    transition: "all 0.3s ease",
    "@keyframes float": {
      "0%": { transform: "translateY(0px)" },
      "50%": { transform: "translateY(-20px)" },
      "100%": { transform: "translateY(0px)" }
    },
    "@keyframes gradient": {
      "0%": { backgroundPosition: "0% 50%" },
      "50%": { backgroundPosition: "100% 50%" },
      "100%": { backgroundPosition: "0% 50%" }
    },
    "&:hover": {
      transform: "scale(1.05) rotate(2deg)",
      textShadow: "4px 4px 8px rgba(0,0,0,0.5)",
    }
  },
  startButton: {
    marginTop: theme.spacing(4),
    padding: theme.spacing(2, 6),
    fontSize: "1.4rem",
    borderRadius: "50px",
    background: "linear-gradient(45deg, #FF6B6B, #4ECDC4)",
    color: "white",
    transition: "all 0.3s ease-in-out",
    boxShadow: "0 4px 15px rgba(0,0,0,0.2)",
    animation: "pulse 2s ease-in-out infinite",
    "@keyframes pulse": {
      "0%": { transform: "scale(1)" },
      "50%": { transform: "scale(1.05)" },
      "100%": { transform: "scale(1)" }
    },
    "&:hover": {
      transform: "scale(1.1) translateY(-5px)",
      boxShadow: "0 6px 20px rgba(0,0,0,0.3)",
      background: "linear-gradient(45deg, #FF8E8E, #5EE8E0)",
    },
    "&:active": {
      transform: "scale(0.95)",
    }
  }
}));

export function Welcome(): JSX.Element {
  const classes = useStyles();

  return (
    <Box className={classes.background}>
      <Container className={classes.content}>
        <Box
          height="100vh"
          display="flex"
          flexDirection="column"
          justifyContent="center"
          alignItems="center"
        >
          <Box width={1}>
            <Typography variant="h1" className={classes.title}>
              Students emotion detector
            </Typography>
          </Box>
          <Box mt={4} width={1} display="flex" alignItems="center" justifyContent="center">
            <Box>
              <CTAButton component={RouterLink} to="/login" className={classes.startButton}>
                Start Journey
              </CTAButton>
            </Box>
          </Box>
        </Box>
      </Container>
    </Box>
  );
}
