import { useEffect } from "react";
import { useAppDispatch, useAppSelector } from "../../reduxHooks";
import { fetchAllMeetings } from "../../meetings/meetingsSlice";
import { Box, Container, Paper, Typography } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import Page from "../../components/Page";
import MeetingTable from "./MeetingTable";
import AddMeetingButton from "./AddMeetingButton";
import { PersonalVideo } from "@material-ui/icons";
import { Hub } from "aws-amplify";

const useStyles = makeStyles((theme) => ({
  videoBackground: {
    position: "fixed",
    right: 0,
    bottom: 0,
    minWidth: "100%",
    minHeight: "100%",
    width: "auto",
    height: "auto",
    zIndex: -1,
    objectFit: "cover",
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: "rgba(0, 0, 0, 0.6)",
    zIndex: -1,
  },
  content: {
    position: "relative",
    zIndex: 1,
    color: "white",
  },
  paper: {
    backgroundColor: "rgba(255, 255, 255, 0.9)",
    backdropFilter: "blur(10px)",
  }
}));

export default function Meetings(): JSX.Element {
  const classes = useStyles();
  const dispatch = useAppDispatch();
  const name = useAppSelector((state) => state.auth.user?.name);

  useEffect(() => {
    const fetchMeetings = async () => {
      dispatch(fetchAllMeetings());
    };

    fetchMeetings();

    const listener = Hub.listen("datastore", async (hubdata) => {
      const { event } = hubdata.payload;
      if (event === "ready") {
        fetchMeetings();
      }
    });

    return () => {
      listener();
    };
  }, [dispatch]);

  return (
    <Page>
      <video
        autoPlay
        loop
        muted
        className={classes.videoBackground}
        playsInline
      >
        <source src="https://assets.mixkit.co/videos/preview/mixkit-students-walking-in-a-university-campus-4519-large.mp4" type="video/mp4" />
      </video>
      <div className={classes.overlay} />
      <Container className={classes.content}>
        <Typography variant="h1">Welcome, {name}</Typography>
        <Box fontSize="h3.fontSize" mb={4}>
          Let's get started! 🚀
        </Box>
        <Typography variant="h2">
          <Box display="flex" alignItems="center">
            <PersonalVideo fontSize="inherit" />
            <Box ml={2}>Your meetings</Box>
          </Box>
        </Typography>
        <Typography variant="body1" paragraph>
          Create a new meeting or choose one from below.
        </Typography>
        <Box>
          <Box mb={2}>
            <AddMeetingButton />
          </Box>
          <Paper className={classes.paper}>
            <MeetingTable />
          </Paper>
        </Box>
      </Container>
    </Page>
  );
}
