import { Box, Button, Container, Typography, makeStyles, Paper, Fade } from "@material-ui/core";
import { useParams } from "react-router-dom";
import Error from "../../error/Error";
import { useState } from "react";
import { Alert, AlertTitle } from "@material-ui/lab";
import { Favorite, Security, Send } from "@material-ui/icons";
import { formatDistanceToNow } from "date-fns";
import NotFound from "../NotFound";
import { useFetchPublicMeeting, useSubmitAnswer } from "./hooks";
import { EXPIRATION_MINUTES } from "./constants";
import { red } from "@material-ui/core/colors";
import { useAppSelector } from "../../reduxHooks";
import Question from "./Question";

const useStyles = makeStyles((theme) => ({
  root: {
    minHeight: "100vh",
    backgroundImage: "url('/images/feedback-bg.jpg.png')",
    backgroundSize: "cover",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    padding: theme.spacing(4),
  },
  feedbackCard: {
    backgroundColor: "rgba(44, 62, 80, 0.85)",
    backdropFilter: "blur(10px)",
    borderRadius: theme.spacing(3),
    padding: theme.spacing(4),
    maxWidth: 600,
    width: "100%",
    border: "1px solid rgba(236, 240, 241, 0.1)",
    boxShadow: "0 8px 32px rgba(0, 0, 0, 0.2)",
  },
  title: {
    color: "#ECF0F1",
    fontSize: "2rem",
    fontWeight: 600,
    marginBottom: theme.spacing(4),
    textAlign: "center",
  },
  submitButton: {
    backgroundColor: "#3498DB",
    color: "#ECF0F1",
    padding: theme.spacing(1.5, 4),
    fontSize: "1.1rem",
    marginTop: theme.spacing(3),
    "&:hover": {
      backgroundColor: "#2980B9",
    },
  },
  anonymousNotice: {
    color: "#BDC3C7",
    fontSize: "0.9rem",
    marginTop: theme.spacing(2),
    textAlign: "center",
  },
  thankYouScreen: {
    textAlign: "center",
    color: "#ECF0F1",
  },
}));

export default function Feedback(): JSX.Element {
  const classes = useStyles();
  const { publicMeetingId } = useParams() as any;
  const [meetingLoading, expired, publicMeeting] =
    useFetchPublicMeeting(publicMeetingId);
  const [submitLoading, submitted, submitAnswer] = useSubmitAnswer(
    publicMeeting?.id,
    publicMeeting?.owner
  );
  const signedIn: boolean = useAppSelector((state) => state.auth.signedIn);
  const userId: string | undefined = useAppSelector(
    (state) => state.auth.user?.id
  );

  const [overallStars, setOverallStars] = useState<number | null>(null);
  const [paceStars, setPaceStars] = useState<number | null>(null);
  const [contentStars, setContentStars] = useState<number | null>(null);

  const handleSubmit = () => {
    submitAnswer(overallStars!, paceStars, contentStars);
  };

  if (submitted) {
    return (
      <Box className={classes.root}>
        <Box className={classes.feedbackCard}>
          <Typography variant="h4" className={classes.thankYouScreen}>
            Thank you for your feedback! üôè
          </Typography>
        </Box>
      </Box>
    );
  }

  return !meetingLoading ? (
    !publicMeeting ? (
      <NotFound />
    ) : (
      <Box className={classes.root}>
        <Box className={classes.feedbackCard}>
          <Typography variant="h4" className={classes.title}>
            How was your experience?
          </Typography>
          <Question
            question="How do you rate the overall experience?"
            value={overallStars}
            onChange={setOverallStars}
            required
          />
          <Question
            question="Was the speaker's pace right for you?"
            value={paceStars}
            onChange={setPaceStars}
          />
          <Question
            question="Was the content useful?"
            value={contentStars}
            onChange={setContentStars}
          />
          <Box display="flex" justifyContent="center">
            <Button
              variant="contained"
              className={classes.submitButton}
              onClick={handleSubmit}
              disabled={!overallStars}
            >
              Submit Feedback
            </Button>
          </Box>
          <Typography variant="body2" className={classes.anonymousNotice}>
            Your feedback is anonymous
          </Typography>
        </Box>
      </Box>
    )
  ) : null;
}
