import Dexie from "dexie";
import ICounterModel from "./counter/CounterModel";

class ErecogDb extends Dexie {
  meetings: Dexie.Table<Meeting, string>;
  ratings: Dexie.Table<Rating, string>;
  counters: Dexie.Table<ICounterModel>;

  constructor() {
    super("erecog_db");

    this.version(1).stores({
      meetings: "id",
      ratings: "id",
      counters: "++id,value",
    });

    this.counters = this.table("counters");
    this.meetings = this.table("meetings");
    this.ratings = this.table("ratings");
  }
}

export interface Meeting {
  id: string;
  name: string;
  startedAt: string;
  stoppedAt?: string;
  owner: string;
}

export interface Rating {
  id: string;
  meetingId: string;
  overallStars: number;
  paceStars?: number;
  contentStars?: number;
  createdAt: string;
}

const db = new ErecogDb();

export default db;
